	TITLE	'Kopieren von MRES-Kassetten Teil 1'	PAGE;	PN	KX;------------------------------------------------------------------;;          KOPIEREN VON MRES-KASSETTEN       TEIL1;;----------------------------------------------------------------	DW	$	DB	'KOPI'	CALL	U1.KTINI	LD	DE,BC.TAB1	LD	HL,UE.BWSA+327H	LD	A,(HL)	CALL	U1.TST	RET	C	LD	(DE),A	INC	HL	XOR	33H	CP	(HL)	RET	NZ	XOR	30H	INC	DE	LD	(DE),A	LD	A,(UE.BWSA+326H)	CP	'J'	JR	Z,$+7	;BEI KOPIJ WERDEN WEITERE PARAMETER	INC	HL	;IGNORIERT	INC	HL	LD	A,(HL)	CP	20H	PUSH	AF	JR	NZ,$+14	CALL	U1.EIN	CALL	U1.KRWE	CALL	U1.AUS	CALL	U1.KRWE	CALL	KX.UPA	POP	AF	JR	Z,$+21	CP	'A'	JR	Z,$+12	CP	'L'	JR	NZ,$+13	CALL	U1.COPB	JP	KX.RSPFR	CALL	U1.COPB2	JR	$-3;-------------------------------------------------------------------	CALL	U1.EIN	CALL	U1.DSVG	CALL	U1.KBR4	CALL	U1.BREAD	LD	HL,UA.Z7E	LD	DE,3163H	LD	BC,31	LDIR	LD	HL,UA.GER	LD	DE,31EAH	CALL	U1.INFO	LD	HL,UA.KNAME+3	LD	DE,323AH	CALL	U1.INFO	LD	HL,UA.NR+3	LD	DE,328AH	CALL	U1.INFO	LD	HL,UA.KOMMT+3	LD	DE,32DAH	CALL	U1.INFO	LD	DE,3203H	LD	A,(BC.TAB1)	CALL	UC.CVBX+2	LD	DE,321BH	LD	A,(BC.TAB1+1)	CALL	UC.CVBX+2	LD	HL,UE.PUF+4	LD	DE,3253H	LD	B,6	CALL	UC.DKONV	LD	HL,UE.PUF+37	LD	DE,32A3H	LD	B,3	CALL	UC.DKONV	LD	DE,32F3H	LD	B,11	CALL	UC.DKONV	CALL	UR.KBV	CALL	UR.KBV	CALL	U1.BREAD	LD	A,(UE.PUF+3)	DEC	A	JP	Z,KX.ENDR	CALL	U1.VERIN	CALL	U1.AUS	CALL	U1.DSVG	CALL	U1.KBR4	LD	(IX+4),02H	CALL	UF.EXEC	LD	HL,UE.PUF+4	LD	DE,326BH	LD	B,6	CALL	UC.DKONV	LD	HL,UE.PUF+37	LD	DE,32BBH	LD	B,3	CALL	UC.DKONV	LD	DE,330BH	LD	B,11	CALL	UC.DKONV	CALL	UR.KBV	CALL	UR.KBV	LD	(IX+4),02H	CALL	UF.EXEC	CALL	U1.BILO	LD	DE,BC.VTABA	CALL	U1.VERNI	CALL	U1.VORST	JP	KY.MDKX.RSPFR:	PUSH	AF	INC	HL	INC	HL	LD	A,(HL)	OR	A	CP	'R'	JR	NZ,$+8	JR	$+3KX.ENDR:	PUSH	AF	SCF	JR	$+6	CP	'F'	JR	NZ,$+23	CALL	U1.EIN	CALL	C,UR.KRW	CALL	UR.KRA	CALL	U1.AUS	CALL	UR.KRE	CALL	C,UR.KRWW	CALL	UR.KRA	POP	AF	RETKX.EXEC2:	PUSH	AF	RES	7,(IX)	LD	(IX+1),0	CALL	KR.KARO	BIT	7,(IX)	JR	Z,$+12	LD	A,(IX+1)	CP	14H	JR	Z,$+7	CALL	UF.ERANZ	POP	AF	RET	RES	7,(IX)	CALL	U1.AUS	CALL	UR.KSL	POP	AF	LD	B,6	INC	SP	DJNZ	$-1	;STACK BERICHTIGEN	JR	KX.RSPFRKX.UPA:	CALL	U1.EIN	CALL	UR.KRE	LD	(IX+0),0	CALL	U1.AUS	CALL	UR.KRE	BIT	2,(IX)	JR	Z,$+7	POP	DE	LD	A,19H	CALL	UF.ERANZ	RET;	END