	TITLE	'Unterprogramme fuer KOPI'	PAGE;	PN	KU;------------------------------------------------------------------;;          UNTERPROGRAMME FUER KOPI;;--------------------------------------------------------KU.VVOR:	PUSH	BC	LD	BC,32	ADD	HL,BC	POP	BC	DJNZ	KU.VVOR	RETKU.TAB2L:	POP	DE	POP	HL	LD	A,(IY+0)	CP	0FFH	JR	Z,$+36	PUSH	IY	LD	B,00H	LD	A,(IY+0)	CP	0FFH	JR	NZ,$+4	JR	$+7	INC	IY	INC	B	JR	$-12	POP	IY	PUSH	IY	LD	A,(IY+1)	LD	(IY+0),A	INC	IY	DJNZ	$-8	POP	IY	POP	BC	DEC	B	LD	A,B	CP	00H	JR	Z,$+5	JP	KY.DFR	JP	KY.ATAAKU.ZEIR:	POP	DE	POP	HL	LD	A,(UB.ZN)	SUB	2	CP	13	JR	NC,$+6	POP	BC	JP	KY.MD	LD	(UB.ZN),A	LD	BC,41	OR	A	SBC	HL,BC	DEC	IY	LD	A,(HL)	CP	(IY+0)	CALL	Z,KU.DECIY	INC	IY	LD	BC,23	OR	A	SBC	HL,BC	POP	BC	INC	B	JR	$-52KU.DECIY:	DEC	IY	RETKU.DD:	PUSH	HL	LD	HL,UA.VORH	CALL	UR.MIMO	JR	C,$+25	LD	A,(HL)	CP	'J'	JR	Z,$+14	DEC	IY	LD	(IY+0),0FFH	LD	DE,80	OR	A	SBC	HL,DE	LD	(HL),20H	LD	HL,UB.ZN	DEC	(HL)	POP	HL	RET	POP	HL	POP	DE	LD	IX,UB.KTAB	JP	KX.ENDR;	END