	TITLE	'Dekomprimierung'	PAGE;	PN	DC;**********************************************************************;*       DCMPR:    Dekomprimierung                                    *;*                 MRES20-Quelltexte   --->   EDIT - Format F         *;**********************************************************************			;DC.DCMPR	EQU	$			; A:  Uebergabe/-nahme Zeichen			; HL: Status-Register DCMPR			; B:  Position (Spalte)			;-------------------------------			; INA: Einzelzeichen-Eingabe			;      nach Reg. A;        --------------------------------------------------------------	INC	B	;         Position + 1	JP	(HL)			;;        --------------------------------------------------------------			; Status "Neue Zeile"			;-------------------------------DC.NL:	CALL	XY.INCDX	;         Satzzaehler + 1	DB	19	LD	A,1EH	;         EDIT-New-Line	LD	B,1	;         Pos.=1	LD	HL,DC.TEXT	;         Status "TEXT"	RET			;;        --------------------------------------------------------------			; Status "Text"			;-------------------------------DC.TEXT:	CALL	UK.GETK	;         Einzelzeichen-Eingabe	OR	A	RET	Z	CP	0AH	;         NL ?	JR	Z,DC.NL	CP	09H	;         TAB ?	JR	Z,DC.TAB	RET			;;        --------------------------------------------------------------			; Tabulator-Behandlung			;-------------------------------DC.TAB:	LD	A,B	CP	40	RET	NC	LD	HL,DC.SPACE	;         ---> Status "Space"	LD	A,' '	;         anstelle TAB Space	RET			;;        --------------------------------------------------------------			; Status "Space"			;-------------------------------DC.SPACE:	LD	A,B	CP	10+1	JR	Z,DC.EOSPC	CP	16+1	JR	Z,DC.EOSPC	CP	35+1	JR	Z,DC.EOSPC	LD	A,' '	RET			;DC.EOSPC:	LD	HL,DC.TEXT	; TAB-Position erreicht	JR	DC.TEXT			;			;;**********************************************************************DC.INIT:	LD	HL,DC.TEXT	; Initialisierung	LD	B,1	RET			;;**********************************************************************;	END